import { Image } from "react-native";
import React from "react";

export default function Avatar({ size, user }) {
  return (
    <Image
      style={{
        width: size,
        height: size,
        borderRadius: size,
      }}
      source={
        user.photoURL
          ? { uri: user.photoURL }
          : require("../assets/user-icon.png")
      }
      resizeMode="cover"
    />
  );
}
