import { View, Text, StyleSheet, Image, FlatList, TouchableOpacity } from 'react-native'
import React, {useState, useEffect} from 'react'
import STATUSDATA from '../data/statusdata'
import { FontAwesome5 } from '@expo/vector-icons';

const Status = () => {
  const [statusData, setStatusData] = useState(STATUSDATA)

  useEffect(() => {
    setStatusData(STATUSDATA)
  }, [])

  const combinedStatusData = [
    {'title': 'Recent updates', data: statusData.filter(item => item.viewed === false)},
    {'title': 'Viewed updates', data: statusData.filter(item => item.viewed === true)},
  ]

  return (
    <View style={styles.myStatusContainer}>
      <View>
        <Image source={require('../../assets/images.jpeg')}/>
      </View>
      <View>
        <Text style={styles.myStatusHeading}>My status</Text>
        <Text style={styles.myStatusSubtext}>Tap to add and update</Text>
      </View>
    </View>
  )
}

export default Status

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
    },
    myStatusContainer: {
      flexDirection: 'row',
      alignItems: 'center',
      padding: 16,
    },
    image: {
      width: 50,
      height: 50,
      borderRadius: 25,
      marginRight: 16
    },
    myStatusHeading: {
      fontSize: 16,
      fontWeight: '600'
    },
    myStatusSubtext: {
      fontSize: 14,
      color: 'gray'
    }
  });
