import { View, Text, StyleSheet, Image, FlatList, TouchableOpacity  } from 'react-native'
import React, {useEffect, useState} from 'react'
import { Fontisto, MaterialIcons, MaterialCommunityIcons } from '@expo/vector-icons';
import DATA from '../data/chartdata';

const Calls = () => {
  const [callData, setCallData] = useState(DATA)

  useEffect(() => {
    setCallData(DATA)
  }, [])

  return (
    <View style={styles.container}>
      <TouchableOpacity>
        <View style={styles.callContainer}>
          <View style={styles.linkContainer}>
            <Fontisto name='link' size={22} color='white'/>
          </View>
          <View>
            <Text style={styles.callLink}>Create a call link</Text>
            <Text style={styles.callSubtext}>Share a link for your whatsApp call</Text>
          </View>
        </View>
      </TouchableOpacity>
      {/* <Text style={styles.recentText}>Recent</Text> */}
      <FlatList
        data={callData}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => (
            <View style={styles.callContainer}>
              <Image source={item.photos} style={styles.image}/>
              <View style={styles.callIcon}>
                <View>

                </View>
              </View>
            </View>
        )}
      />
    </View>
  )
}

export default Calls

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
    },
    callContainer: {
      flexDirection:'row',
      alignItems:'center',
      padding: 16,
    },
    linkContainer: {
      width: 50,
      height: 50,
      borderRadius: 25,
      backgroundColor: '#128c7e',
      alignItems: 'center',
      justifyContent: 'center',
      marginRight: 16
    },
    callLink: {
      fontSize: 16,
      fontWeight: '600',
    },
    callSubtext: {
      fontSize: 14,
      color: 'grey'
    }
  });
